import React, {Component} from "react"
import bank from '../bank.png'
import Web3 from "web3"
import { idText } from "typescript"

class Navbar extends Component {
    async Web3() {
        if(window.ethereum) {
            window.web3 = new Web3(window.ethereum)
            await window.ethereum.enable()
        }else if(window.web3){
            window.web3= new Web3(window.web3.currentProvider)
        
        }else{
            window.alert('No ethereum browser detected!')
        }
    }
    render(){
        return(
            <nav className='navbar navbar-dark fixed-top shadow p-0' style={{backgroundColor:'black', height:'50px'}}>
                <a className='navbar-brand col-sm-3 col-md-2 mr-0' style={{color:'white'}}>
                <img src={bank} width='50' height='30' className='d-inline-block align-top' alt=''>
                </img>
                &nbsp; Dapp Yield Staking (Decentralised Banking) 
                    </a>

                <ul className='navbar-nav px-3'>
                    <li className='texy-nowrap d-none nav-item d-sm-none d-sm-block'>
                        <small style={{color:"white"}}>ACCOUNT NUMBER: {this.props.account}

                        </small>
                    </li>
                </ul>


            </nav>
        )
    }
}
export default Navbar;
